mapper.select:
SELECT id, province, city, company_name, tel, seedling_type,
seedling_name, specification, unit, quantity, price, comments,
acquisition_time
FROM gs_seedling_info


        AND seedling_name LIKE '%${keyWord}%'


        AND seedling_type = #{tp}


        AND company_name LIKE '%${comp}%'


        AND province = #{province}


        AND city = #{city}


        AND address = #{address}


        AND specification LIKE '%${spec}%'


        AND price <= #{maxPrice}


        AND price <= #{maxPrice}


ORDER BY sid DESC limit #{num},20;
